condition.value:
@and(
  equals(item().type, 'Folder'),
  and(
    greaterOrEquals(length(item().name), 20),
    and(
      equals(substring(item().name, 4, 1), '-'),
      and(
        equals(substring(item().name, 7, 1), '-'),
        and(
          equals(substring(item().name, 10, 1), 'T'),
          and(
            equals(substring(item().name, 13, 1), ':'),
            and(
              equals(substring(item().name, 16, 1), ':'),
              endswith(item().name, 'Z')
            )
          )
        )
      )
    )
  )
)

body.value:
{
    "pipeline_name": "@{pipeline().Pipeline}",
